<script>
import { ref, onMounted } from 'vue';
import { useRouter } from 'vue-router';
import ThemeToggle from '../../../shared/components/theme-toggle.component.vue';
import LanguageSwitcher from '../../../shared/components/language-switcher.component.vue';
import { useI18n } from 'vue-i18n';
import Button from '../../../shared/components/button.component.vue';
import AuthService from '../services/auth.service';
import { reactive } from 'vue';

export default {
  name: 'login',
  components: {
    ThemeToggle,
    LanguageSwitcher,
    Button
  },
  setup() {
    const { t } = useI18n();
    const router = useRouter();
    const showPassword = ref(false);
    const error = ref('');
    const loading = ref(false);

    const loginForm = reactive({
      username: '',
      password: ''
    });

    const togglePassword = () => {
      showPassword.value = !showPassword.value;
    };

    const login = async (loginForm) => {
      if (!loginForm.username || !loginForm.password) {
        error.value = t('authentication.errors.all_fields');
        return;
      }
      
      loading.value = true;
      error.value = '';

      try {
        console.log('üîê Intentando login con:', {
          username: loginForm.username,
          password: '***'
        });
        
        const result = await AuthService.login(loginForm);
        console.log('‚úÖ Login exitoso:', result);
        
        // Redirigir despu√©s de un breve delay para mostrar el mensaje de √©xito
        setTimeout(() => {
          router.push('/');
        }, 500);
        
      } catch (error) {
        console.error('‚ùå Error en login:', error);
        error.value = error.message;
      } finally {
        loading.value = false;
      }
    };



    return {
      t,
      showPassword,
      loginForm,
      error,
      loading,
      togglePassword,
      login
    };
  },
};
</script>

