        <h1>{{ t('authentication.login') }}</h1>
        <p class="welcome-text">{{ t('authentication.back') }}</p>

        <div v-if="error" class="error-message">
          {{ error }}
        </div>

        <label for="username">{{ t('authentication.username') }}</label>
        <div class="input-container">
          <input 
            type="text" 
            id="username" 
            v-model="loginForm.username"
            :placeholder="t('authentication.username_placeholder')"
          />
        </div>

        <label for="password">{{ t('authentication.password') }}</label>
        <div class="input-container">
          <input  
            :type="showPassword ? 'text' : 'password'"
            id="password"
            v-model="loginForm.password"
            :placeholder="t('authentication.password_placeholder')"
          />
          <button 
            type="button" 
            id="togglePassword" 
            class="toggle-icon"  
            aria-label="Mostrar/Ocultar contrase√±a" 
            @click="togglePassword"
